<?phpnamespace Think\Weixin\WechatPhpSdk;class Menu {		// 开发者中心-配置项-AppID(应用ID)	protected $appId;	protected $appSecret;		/**     * 设定配置项     *     * @param array $config     */    public function __construct($config) {        $this->appId	  		= 	$config['appId'];        $this->appSecret	  		= 	$config['appSecret'];    }    	public function getAccessToken() //获取access_token  	{  		$url = "https://api.weixin.qq.com/cgi-bin/token?grant_type=client_credential&appid=".$this->appId."&secret=".$this->appSecret;  		$data = $this -> getCurl($url);//通过自定义函数getCurl得到https的内容  		$resultArr = json_decode($data, true);//转为数组  		return $resultArr["access_token"];//获取access_token  	}  	public function creatMenu($menulist)//创建菜单  	{  		$accessToken = $this->getAccessToken();//获取access_token  				$menuPostUrl = "https://api.weixin.qq.com/cgi-bin/menu/create?access_token=".$accessToken;//POST的url  				$menu = $this -> dataPost($menulist, $menuPostUrl);//将菜单结构体POST给微信服务器 		return $menu;			}  		public function getCurl($url){//get https的内容  		$ch = curl_init();  		curl_setopt($ch, CURLOPT_URL,$url);  		curl_setopt($ch, CURLOPT_RETURNTRANSFER,1);//不输出内容  		curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);  		curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, false);  		$result =  curl_exec($ch);  		curl_close ($ch);  		return $result;  	}  	public function dataPost($post_string, $url) {//POST方式提交数据  		$context = array ('http' => array ('method' => "POST", 'header' => "User-Agent: Mozilla/5.0 (Windows; U; Windows NT 6.0; en-US) \r\n Accept: */*", 'content' => $post_string ) );  		$stream_context = stream_context_create ( $context );  		$data = file_get_contents ( $url, FALSE, $stream_context );  		return $data;  	}  }